### 1.词法分析

```c
#include <stdio.h>
#include <string.h>
#include <conio.h>  // getch()
#include <stdlib.h>

char prog[80], token[32], ch;  // 扩大 token 的大小，防止溢出
int syn, p, m, n, sum;
char *rwtab[6] = {"begin", "if", "then", "while", "do", "end"};  // 保留字表

void scaner();

int main() {
    p = 0;
    printf("\nPlease input a string (end with '#'):\n");

    // 读取输入直到#
    do {
        scanf("%c", &ch);
        prog[p++] = ch;
    } while (ch != '#');

    p = 0;
    do {
        scaner();  // 执行词法分析
        switch (syn) {
            case 11:
                printf("( %-10d%5d )\n", sum, syn);
                break;
            case -1:
                printf("You have input a wrong string\n");
                getch();
                exit(0);
            default:
                printf("( %-10s%5d )\n", token, syn);
                break;
        }
    } while (syn != 0);  // 直到遇到终止符号 #

    getch();
}

// 词法分析函数
void scaner() {
    sum = 0;
    memset(token, 0, sizeof(token));  // 清空token数组
    ch = prog[p++];
    m = 0;

    // 跳过空格和换行符
    while ((ch == ' ') || (ch == '\n')) {
        ch = prog[p++];
    }

    // 处理标识符或保留字
    if (((ch >= 'a') && (ch <= 'z')) || ((ch >= 'A') && (ch <= 'Z'))) {
        while (((ch >= 'a') && (ch <= 'z')) || ((ch >= 'A') && (ch <= 'Z')) || ((ch >= '0') && (ch <= '9'))) {
            token[m++] = ch;
            ch = prog[p++];
        }
        p--;  // 回退一个字符
        syn = 10;  // 标识符默认值

        // 检查是否是保留字
        for (n = 0; n < 6; n++) {
            if (strcmp(token, rwtab[n]) == 0) {
                syn = n + 1;  // 是保留字
                break;
            }
        }
    }
        // 处理数字
    else if ((ch >= '0') && (ch <= '9')) {
        while ((ch >= '0') && (ch <= '9')) {
            sum = sum * 10 + ch - '0';
            ch = prog[p++];
        }
        p--;  // 回退一个字符
        syn = 11;  // 数字
    }
        // 处理其他符号
    else {
        token[m++] = ch;
        switch (ch) {
            case '<':
                ch = prog[p++];
                if (ch == '=') {
                    syn = 22;
                    token[m++] = ch;
                } else {
                    syn = 20;
                    p--;
                }
                break;
            case '>':
                ch = prog[p++];
                if (ch == '=') {
                    syn = 24;
                    token[m++] = ch;
                } else {
                    syn = 23;
                    p--;
                }
                break;
            case '+':
                ch = prog[p++];
                if (ch == '+') {
                    syn = 17;
                    token[m++] = ch;
                } else {
                    syn = 13;
                    p--;
                }
                break;
            case '-':
                ch = prog[p++];
                if (ch == '-') {
                    syn = 29;
                    token[m++] = ch;
                } else {
                    syn = 14;
                    p--;
                }
                break;
            case '!':
                ch = prog[p++];
                if (ch == '=') {
                    syn = 21;
                    token[m++] = ch;
                } else {
                    syn = 31;
                    p--;
                }
                break;
            case '=':
                ch = prog[p++];
                if (ch == '=') {
                    syn = 25;
                    token[m++] = ch;
                } else {
                    syn = 18;
                    p--;
                }
                break;
            case '*': syn = 15; break;
            case '/': syn = 16; break;
            case '(': syn = 27; break;
            case ')': syn = 28; break;
            case '{': syn = 5; break;
            case '}': syn = 6; break;
            case ';': syn = 26; break;
            case '\"': syn = 30; break;
            case '#': syn = 0; break;
            case ':': syn = 17; break;
            default: syn = -1; break;
        }
    }
    token[m] = '\0';  // 结束token字符串
}

