#include <conio.h>
#include <stdio.h>
#include <string.h>

char prog[100], token[8], ch;
char *rwtab[6] = {"begin", "if", "then", "while", "do", "end"};
int syn, p, m, n, sum, kk;

// 函数声明
void scaner();
int lrparser();
int yucu();
int statement();
int expression();
int term();
int factor();

int main() {
    p = kk = 0;
    printf("\nPlease input a string (end with '#'): \n");

    // 读入程序字符
    do {
        scanf("%c", &ch);
        prog[p++] = ch;
    } while (ch != '#');

    p = 0;
    scaner(); // 读取第一个单词符号
    lrparser(); // 进行语法分析
    getch();
    return 0;
}

// LR语法分析器
int lrparser() {
    if (syn == 1) { // 'begin'
        scaner();       // 读下一个单词符号
        yucu();         // 调用语句序列处理
        if (syn == 6) { // 'end'
            scaner();
            if (syn == 0) { // 成功
                printf("Success!\n");
            }
        } else {
            printf("Error: Expected 'end'!\n");
        }
    } else {
        printf("Error: Expected 'begin'!\n");
    }
    return 0;
}

// 处理语句序列
int yucu() {
